			            {{#if type '==' 'top'}}
			                {{#if products.top_sellers}}
			                    {{> components/products/top-2 products=products.top_sellers columns=theme_settings.homepage_top_products_column_count productsnew=products.new show_dots_bar="true" id="TopSellerCarousel"}}
			                {{/if}}
