            </article>

            <article class="footer-info-col footer-info-col--small" data-section-type="footer-webPages">
                <h3 class="footer-info-heading footer-info-heading--toggle">{{theme_settings.footer-info-2-heading}}</h3>
                <ul class="footer-info-list">
                    {{#if settings.gift_certificates_enabled}}
                        <li>
                            <a href="{{urls.gift_certificate.purchase}}"
                               aria-label="{{lang 'common.gift_cert'}}"
                            >
                                {{lang 'common.gift_cert'}}
                            </a>
                        </li>
                    {{/if}}
                    {{#each pages}}
                        {{#if name '!=' 'Custom Tab'}}
                            <li>
                                <a href="{{url}}" aria-label="{{name}}">{{name}}</a>
                            </li>
                        {{/if}}
                    {{/each}}
                </ul>
            </article>

            <article class="footer-info-col footer-info-col--small" data-section-type="footer-webPages">
                <h3 class="footer-info-heading footer-info-heading--toggle">{{theme_settings.footer-info-3-heading}}</h3>
                <ul class="footer-info-list">
                    {{#if theme_settings.footer-info-3-text1}}
