components/halothemes/category/halo-subc:
{{#if category.subcategories}}
    <div class="haloSubCategories">
        <div class="haloSubCategories__container">
            <div class="haloSubCategories__carousel"
                data-slick='{
                    "dots": false,
                    "arrows": true,
                    "infinite": false,
                    "mobileFirst": true,
                    "slidesToShow": 1,
                    "slidesToScroll": 1,
                    "responsive": [
                        {
                            "breakpoint": 481,
                            "settings": {
                                "slidesToShow": 2,
                                "slidesToScroll": 2
                            }
                        },
                        {
                            "breakpoint": 768,
                            "settings": {
                                "slidesToShow": 3,
                                "slidesToScroll": 3
                            }
                        },
                        {
                            "breakpoint": 992,
                            "settings": {
                                "slidesToShow": 4,
                                "slidesToScroll": 4
                            }
                        },
                        {
                            "breakpoint": 1200,
                            "settings": {
                                "slidesToShow": 5,
                                "slidesToScroll": 5
                            }
                        }
                    ]
                }'
            >
                {{#each category.subcategories}}
                    <div class="haloSubCategories__item">
                        {{#if image}}
                            <div class="haloSubCategories__img">
                                <a href="{{url}}" aria-label="Button Go To Sub Category {{name}}">
                                    <img class="lazyload" src="{{cdn 'img/loading.svg'}}" data-src="{{getImage image '80x80'}}" alt="{{name}}">
                                </a>
                            </div>
                        {{else}}
                            <div class="haloSubCategories__img">
                                <a href="{{url}}" aria-label="Button Go To Sub Category {{name}}">
                                    <span class="halo-comingSoon">{{../theme_settings.halo_coming_soon_text}}</span>
                                </a>
                            </div>
                        {{/if}}
                        <div class="haloSubCategories__content">
                            <a class="haloSubCategories__name" href="{{url}}" aria-label="Button Go To Sub Category {{name}}">{{name}}</a>
                            <div class="haloSubCategories__count">{{product_count}} {{#if product_count '>=' 2}}products{{else}}product{{/if}}</div>
                            {{#if description}}
                                <div class="haloSubCategories__des">{{{description}}}</div>
                            {{/if}}
                        </div>
                    </div>
                {{/each}}
            </div>
        </div>
    </div>
{{/if}}
