components/halothemes/sidebar/halo-side-:
{{#if theme_settings.sidebar_image}}
    <div id="sideBannerImage" class="sidebarBlock">
    	<div class="side-cms-img">
    		<a class="cms-image" href="{{theme_settings.sidebar_image_link}}">
                <img class="lazyload" src="{{cdn 'img/loading.svg'}}" data-src="https://cdn11.bigcommerce.com/s-{{settings.store_hash}}/product_images/uploaded_images/{{theme_settings.sidebar_image}}" alt="{{theme_settings.sidebar_image}}" />
    		</a>
    	</div>
    </div>
{{/if}}

{{#if theme_settings.show_sidebar_custom_block}}
	<div id="sideCustomCMS" class="sidebarBlock">
		{{#if theme_settings.sidebar_custom_block_title '!=' ''}}
	    	<h5 class="sidebarBlock-heading"><span>{{theme_settings.sidebar_custom_block_title}}</span></h5>
	    {{/if}}
	    <div class="side-cms-content">
	    	{{#if theme_settings.sidebar_custom_block_content_1}}
	        	<p>{{theme_settings.sidebar_custom_block_content_1}}</p>
	        {{/if}}
	        {{#if theme_settings.sidebar_custom_block_content_2}}
	        	<p>{{theme_settings.sidebar_custom_block_content_2}}</p>
	        {{/if}}	
	    </div>
	</div>
{{/if}}

{{#if theme_settings.side_all_categories}}
    <div class="sidebarBlock" id="sideAllCategories">
        <h5 class="sidebarBlock-heading">Categories</h5>
        {{#if categories}}
        <ul class="all-categories-list">
            {{#each categories}}
            <li class="navPages-item">
                {{#if children}}
                <a class="navPages-action has-subMenu" href="{{url}}">
                    <span>{{name}}</span>
                </a>
                <i class="icon-dropdown" aria-hidden="true"><svg><use xlink:href="#icon-chevron-right" /></svg></i>
                <ul class="dropdown-category-list navPages-level-2">
                    {{#each children}}
                        <li class="navPage-subMenu-item">
                            {{#if children}}
                                <a class="navPage-subMenu-action navPages-action has-subMenu" href="{{url}}">
                                    <span>{{name}}</span>
                                </a>
                                <i class="icon-dropdown" aria-hidden="true"><svg><use xlink:href="#icon-chevron-right" /></svg></i>
                                <ul class="dropdown-category-list navPages-level-3">
                                    {{#each children}}
                                    <li class="navPage-childList-item">
                                        {{#if children}}
                                        <a class="navPage-childList-action navPages-action has-subMenu" href="{{url}}"><span>{{name}}</span></a>
                                        <i class="icon-dropdown" aria-hidden="true"><svg><use xlink:href="#icon-chevron-right" /></svg></i>
                                        <ul class="dropdown-category-list navPages-level-4">
                                            {{#each children}}
                                            <li class="navPage-childList-item">
                                                {{#if children}}
                                                    <a class="navPage-childList-action navPages-action has-subMenu" href="{{url}}"><span>{{name}}</span></a>
                                                    <i class="icon-dropdown" aria-hidden="true"><svg><use xlink:href="#icon-chevron-right" /></svg></i>
                                                    <ul class="dropdown-category-list navPages-level-5">
                                                        {{#each children}}
                                                         <li class="navPage-childList-item">
                                                             <a class="navPage-childList-action navPages-action " href="{{url}}"><span>{{name}}</span></a>
                                                         </li>
                                                         {{/each}}
                                                    </ul>
                                                {{else}}
                                                <a class="navPage-childList-action navPages-action " href="{{url}}"><span>{{name}}</span></a>
                                                {{/if}}
                                            </li>
                                            {{/each}}
                                        </ul>
                                        {{else}}
                                        <a class="navPage-childList-action navPages-action " href="{{url}}"><span>{{name}}</span></a>
                                        {{/if}}
                                    </li>
                                    {{/each}}
                                </ul>
                            {{else}}
                                <a class="navPage-subMenu-action navPages-action" href="{{url}}"><span>{{name}}</span></a>
                            {{/if}}
                        </li>
                    {{/each}}
                </ul>
                {{else}}
                <a class="navPages-action" href="{{url}}"><span>{{name}}</span></a>
                {{/if}}
            </li>
            {{/each}}
        </ul>
        {{/if}}
    </div>
{{/if}}

components/faceted-search/facets/hierarc:
{{#if items}}
<div class="accordion-block">
    {{> components/faceted-search/faceted-search-navigation}}

    <div id="facetedSearch-content--{{dashcase facet}}" class="accordion-content{{#unless start_collapsed}} is-open{{/unless}}">
        <ul id="facetedSearch-navList--{{dashcase facet}}" class="navList" data-facet="{{facet}}" data-has-more-results="{{has_more_results}}">
            {{#each items}}
                <li class="navList-item">
                    <a
                        href="{{url}}"
                        class="navList-action navList-action--checkbox{{#if selected }} is-selected{{/if}}{{#if has_sub_categories}} has-children{{/if}}"
                        rel="nofollow"
                        data-id="{{id}}"
                        data-faceted-search-facet>
                        {{{sanitize title}}}
                        {{#if ../show_product_counts}}
                            {{#if count}}
                                <span>({{count}})</span>
                            {{/if}}
                        {{/if}}
                    </a>
                    {{#if children}}
                        {{> components/faceted-search/facets/hierarchy-children children}}
                    {{/if}}
                </li>
            {{/each}}
        </ul>

        {{#if show_more_toggle}}
            <a href="#facetedSearch-navList--{{dashcase facet}}" role="button" class="toggleLink">
                <span class="toggleLink-text toggleLink-text--off">{{lang 'search.faceted.show-more'}}</span>
            </a>
        {{/if}}
    </div>
</div>
{{/if}}

